import logo from './logo.svg';
import './App.css';
import React,{useEffect,useState} from 'react';
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import styled from "styled-components";
import Nav from './components/includes/Nav';
import Dashboard from './components/screens/Dashboard';
import Profile from './components/includes/Profile';
import Notification from './components/screens/Notification';
import WantBlood from './components/screens/WantBlood';
import History from './components/screens/History';
import axios from 'axios';

function App() {
  const [state, setState] = useState({
    details: [],
    user: '',
    quote: '',
});

useEffect(() => {
    let data;

    axios
        .get('http://localhost:8000/dashboard/')
        .then((res) => {
            data = res.data;
            setState((prevState) => ({
                ...prevState,
                details: data,
            }));
        })
        .catch((err) => {
            // Handle errors if needed
        });
}, []); // Empty dependency array to run the effect only once (equivalent to componentDidMount)

// Rest of your functional component logic

  return (
    <Router>
      <Container>
        <Nav />
        <RouteContainer>
          <Routes>
            <Route path='dashboard' element={<Dashboard/>} />
            <Route path='notification' element={<Notification/>} />
            <Route path='want-blood' element={<WantBlood/>} />
            <Route path='history' element={<History/>} />
          </Routes>
        </RouteContainer>
        <Profile />
      </Container>
    </Router>
  );
}

export default App;
const Container = styled.div`
  display: flex;
`
const RouteContainer = styled.div`
  padding: 55px 30px 0px;
  width: 77%;
  padding-left: 12%;
`
